import React, { useState } from 'react';

function ContactUs() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Assuming you want to display a thank you message to the user
    // You can customize this logic as needed

    // Display a thank you message by updating the state
    setFormData({
      name: '',
      email: '',
      message: '',
    });
    setSubmitted(true);

    // Optional: You can also send the form data to a server using fetch or axios here
    // Example with fetch:
    /*
    try {
      const response = await fetch('your_server_endpoint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Handle a successful server response, if needed
      } else {
        // Handle errors from the server, if needed
      }
    } catch (error) {
      // Handle any network errors, if needed
    }
    */
  };

  return (
    <div className='main-content'>
      <div className='contact'>
        <h2>Contact Us</h2>
        {submitted ? (
          <p>Thank you for your submission! We'll get back to you soon.</p>
        ) : (
          <form onSubmit={handleSubmit}>
            <div className='form-group'>
              <label htmlFor='name'>Name:</label>
              <input
                type='text'
                id='name'
                name='name'
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className='form-group'>
              <label htmlFor='email'>Email:</label>
              <input
                type='email'
                id='email'
                name='email'
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className='form-group'>
              <label htmlFor='message'>Message:</label>
              <textarea
                id='message'
                name='message'
                value={formData.message}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type='submit'>Submit</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default ContactUs;
